SELECT
  bno, title, content, writer, regdate, updatedate
FROM
    (SELECT /*+INDEX_DESC(tbl_free_board pk_free_board) */
      rownum rn, bno, title, content, writer, regdate, updatedate
    FROM
      tbl_free_board
    WHERE rownum <= #{page} * #{amount}
    )
WHERE rn > (#{page} -1) * #{amount}
